mapper.select:
  		SELECT * FROM
  		(
	  		SELECT ROWNUM AS RN, tbl.* FROM
		  		(
		  		SELECT * FROM tbl_reply
		  		WHERE bno = #{boardNo}
		  		ORDER BY reply_no DESC
		  		) tbl
  		)

	  	WHERE rn > #{paging.pageStart} AND rn <= #{paging.pageEnd}

SELECT COUNT(*)
FROM tbl_reply
WHERE bno = #{bno}

SELECT reply_pw
FROM tbl_reply
WHERE reply_no = #{rno}

mapper.update:
UPDATE tbl_reply
SET reply_text = #{replyText}, update_date = sysdate
WHERE reply_no = #{replyNo}
